import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  // Modifier le template pour affichier le message suivant : Liste de Pokémons
  template: `<h1>Welcome to {{ pokemonList[0] }}!</h1>`,
})

export class AppComponent implements OnInit {
  // Charger la liste des 12 pokemons 
  pokemonList = ['Pikachu', 'Salamèche', 'Carapuce', 'Bulbizarre'];

  ngOnInit() {
    console.table(this.pokemonList);
    this.selectPokemon('Pikachu');
  }

  selectPokemon(pokemonName: string) {
    console.log(`Vous avez choisi ${pokemonName} !`);
  }
}
