import { Component, OnInit } from "@angular/core";
import { POKEMONS } from "./mock-pokemon-list";
import { Pokemon } from "./pokemon";

@Component({
  selector: "app-root",
  // Modifier le template pour affichier le message suivant : Liste de Pokémons
  templateUrl: "./app.component.html",
})
export class AppComponent implements OnInit {
  // Charger la liste des 12 pokemons dans le composant
  pokemonList: Pokemon[] = POKEMONS;
  pokemonSelected: Pokemon | undefined;

  ngOnInit() {
    console.table(this.pokemonList);
  }

  // Passer en paramètre de la méthode selectPokemon un pokemon issue du mock de pokemon créee précédemment
  selectPokemon(pokemonId: string) {
    const pokemon: Pokemon | undefined = this.pokemonList.find(
      (pokemon) => pokemon.id == +pokemonId
    );

    if (pokemon) {
      console.log(`Vous avez demandé le pokemon ${pokemon.name} !`);
      this.pokemonSelected = pokemon;
    } else {
      console.log(`Le pokemon demandé n'existe pas !`);
      this.pokemonSelected = pokemon;
    }
  }
}
<h1 class="center"> Liste des Pokémons </h1>

<div class="container">
    <div class="row">
        <div *ngFor="let pokemon of pokemonList" class="col m4 s6" pokemonBorderCard>
            <div class="card horizontal">
                <div class="card-image">
                    <img [src]="pokemon.picture">
                </div>
                <div class="card-stacked">
                    <div class="card-content">
                        <p>{{ pokemon.name }}</p>
                        <p><small>{{ pokemon.created | date: 'dd/MM/yyyy' }}</small></p>
                        <span *ngFor="let type of pokemon">
                        
                        </span>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
